import React, { Component } from "react";
import { Button } from "reactstrap";

class DlButton extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  savedRender = [];
  textFile = null;

  makeTextFile = text => {
    var data = new Blob([text], { type: "text/plain" });

    // If we are replacing a previously generated file we need to
    // manually revoke the object URL to avoid memory leaks.
    if (this.textFile !== null) {
      window.URL.revokeObjectURL(this.textFile);
    }
    this.textFile = window.URL.createObjectURL(data);
    // returns a URL you can use as a href
    return this.textFile;
  };

  saveFile = () => {
    this.downloadURI(
      this.makeTextFile(JSON.stringify(this.savedRender)),
      "shapes.json"
    );
  };
  downloadURI = (uri, name) => {
    var link = document.createElement("a");
    link.download = name;
    link.href = uri;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
  saveImage = () => {
    const ctx = this.stageRef.current.children[1].getContext("2d");
    console.log(ctx);

    const dataURL = this.stageRef.current
      .getStage()
      .toDataURL({ pixelRatio: 3 });
    this.downloadURI(dataURL, "stage.png");
  };

  render() {
    return (
      <>
        <Button onClick={this.saveImage}>Save Image</Button>
        <Button onClick={this.saveFile}>Save set</Button>

        <div id="create"></div>
      </>
    );
  }
}

export default DlButton;
