import React, { Component } from "react";
import Canvg from "canvg";
import logo from "./logo.svg";
import icon from "../ic.svg";

class Canvas extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  v = null;
  componentDidMount() {
    this.load();
  }
  load = async () => {
    const canvas = document.querySelector("canvas");
    const ctx = canvas.getContext("2d");

    this.v = await Canvg.from(ctx, icon);
    console.log(this.v, ctx);

    // Start SVG rendering with animations and mouse handling.
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    canvg(document.getElementById("canvas"), icon, {
      ignoreClear: true,
      ignoreMouse: true
    });
    canvg(document.getElementById("canvas"), logo, {
      ignoreClear: true,
      ignoreMouse: true
    });
    this.v.start();
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
  };

  componentWillUnmount() {
    this.v.stop();
  }
  render() {
    return (
      <div style={{ margin: 20 }}>
        render
        <canvas width="3000" height="2000"></canvas>
        <img src={logo} alt="" />
      </div>
    );
  }
}

export default Canvas;
