  //       layer.add(image);
  //       layer.batchDraw();
  //     }
  //   });

  v = null;
  componentDidMount() {
    this.load();
  }
  load = async () => {
    this.stage.add(layer);
    const canvas = document.querySelector("canvas");
    const ctx = canvas.getContext("2d");

    // this.v = await Canvg.from(ctx, icon);
