          image: canvas,
          x: 200,
          width: 150,
          height: 150
        });
        this.layer.add(image);
        this.layer.batchDraw();
      }
    });
  };

  componentWillUnmount() {
    this.v.stop();
  }
  render() {
    return (
      <div style={{ margin: 20 }}>
        render
        <canvas width="3000" height="2000"></canvas>
        <img src={logo} alt="" />
      </div>
    );
  }
}

export default Canvas;
